import styled from 'styled-components';


const IntroStyle = styled.div`
    a{
        text-decoration:none;
    }
    .colum1{
        width:100%;
        img{
            width:100%;
        }
    }
    .element{
        margin-top:10%;
        margin-left: 5%;
    }
    .colum2{
        width:90%;
        margin: 0 auto;
        margin-top:10%;
    }
    .button{
        display:flex;
        margin-top:15%;
        margin-bottom: 20%;
    }
    .line{
        width:20%;
        margin-left: 3%;
    }
    .title{
        margin-bottom:20%;
    }


    @media only screen and (min-width:415px) and (max-width:768px){
        .element{
            margin-top:8%;
            margin-left: 5%;
        }
        .colum2{
            margin-top:5%;
        }
        .title{
            margin-bottom:10%;
        }
        .line{
            width:15%;
        }
        .button{
            display:flex;
            margin-top:13%;
            margin-bottom: 15%;
        }


    }
    @media only screen and (min-width:769px) and (max-width:1024px){
        display:flex;
        .colum1{
            width:50%;
        }
        .colum2{
            width:50%;
        }
        .title{
            margin-bottom:10%;
        }
    }
    @media only screen and (min-width:1025px) and (max-width:1440px){

    }
    @media only screen and (min-width:1441px){

    }

`;

export { IntroStyle };
