import styled from 'styled-components';


const FooterStyle = styled.div`
    a{
        text-decoration:none;
    }
    border-top: 1px solid black;
    margin:0 auto;
    display:flex;
    flex-flow: column;
    width:94%;


    .footer{
        width:95%;
        margin:0 auto;
        display:flex;
        flex-flow:column;
        justify-content:space-between;
        padding-top: 9%;
    }
    .option{

        display:flex;
        flex-flow:column;
        justify-content:space-between;
        width:65%;
    }
    .len{
        display:flex;
        justify-content:space-between;
        width:40%;
        cursor: pointer;
    }
    .copy{
        padding-top: 9%;
        width:95%;
        margin:0 auto;
    }

    @media only screen and (min-width:415px) and (max-width:768px){
        .footer, .option{
            flex-flow:row;
        }
        .option{
            width:70%;
        }
        .len{
            width:30%;
        }
    }
    @media only screen and (min-width:769px) and (max-width:1024px){

    }
    @media only screen and (min-width:1025px) and (max-width:1440px){

    }
    @media only screen and (min-width:1441px){
       width:80%;


    .footer{
        width:95%;
        margin:0 auto;
        display:flex;
        justify-content:space-between;
    }
    .option{
        display:flex;
        justify-content:space-between;
        width:65%;
    }
    .len{
        display:flex;
        justify-content:space-between;
        width:10%;
        cursor: pointer;
    }
    .copy{
        width:95%;
        margin:0 auto;
    }
    }

`;

export { FooterStyle };
