import React, { useState } from "react";
import Link from "next/link";

import { SearchStyle } from "./SearchStyle";
import Button from "../Button/Button";



import ParagraphBody from "../../styles/fontsStyles/paragraphBody";
import ParagraphTitle from "../../styles/fontsStyles/paragraphTitle";
import ParagraphHeader from "../../styles/fontsStyles/paragraphHeader"


import { i18n, withTranslation } from '../../i18n'

const changeLanguage = (language, setLangauge) => {
    i18n.changeLanguage(language)
    setLangauge(language)
}


const Search = props => {
    const { t } = props;
    const [language, setLangauge] = useState(i18n.language)

    return (
        <SearchStyle>
            <div className="img">
                <img src="/static/svg/iconOlas.svg" />
                <div className="imgIcon">
                    <img src="/static/svg/iconWord.svg" />
                    <div>
                        <img src="/static/svg/iconTicket.svg" />
                    </div>
                </div>
            </div>

            <div className="text">
                <ParagraphTitle align="center">{t("intro1")}</ParagraphTitle>
                <ParagraphHeader align="center">{t("subTitle1")}</ParagraphHeader>
                <ParagraphBody align="center">{t("description1")}</ParagraphBody>
                <Button content={t("button2")} color="white"/>
            </div>

        </SearchStyle>
    );
};

export default withTranslation("home")(Search);
