import React, { useState } from "react";
import Link from "next/link";

import { FooterStyle } from "./FooterStyle";
import footer from "../../styles/fontsStyles/footer";



import { i18n, withTranslation } from '../../i18n'

const changeLanguage = (language, setLangauge) => {
    i18n.changeLanguage(language)
    setLangauge(language)
}


const Footer = props => {
    const { t } = props;
    const [language, setLangauge] = useState(i18n.language)
    const email = t("email");
    const phone = t("phone");
    return (
        <FooterStyle backgroundColor={props.backgroundColor} >
            <div className="option">
                <a href={`mailto:${email}`}><footer>{t("email")}</footer></a>
                <a href={`tel:${phone}`}><footer>{t("phone")}</footer></a>
                <a href="/"><footer>{t("termsConditions")}</footer></a>
                <a href="/"><footer>{t("privacyPolicy")}</footer></a>
            </div>
            <div className="len">
                <footer decoration={language === 'es' ? "underline" : ""} cursor onClick={() => changeLanguage('es', setLangauge)}>Español</footer>
                <footer>/</footer>
                <footer decoration={language === 'en' ? "underline" : ""} cursor onClick={() => changeLanguage('en', setLangauge)}>English</footer>
            </div>

            <h3>
                Copyright © by Tailor hub S.L, 2019. All rights reserved. All materials
                on this website are intellectual property of Tailor hub S.L.
            </h3>
        </FooterStyle>
    );
};

export default withTranslation("footer")(Footer);
