import styled from 'styled-components';


const IntroStyle = styled.div`
    a{
        text-decoration:none;
    }
    .colum1{
        width:100%;
        img{
            width:100%;
        }
    }
    .colum2{
        width:90%;
        margin: 0 auto;
        margin-top:15%;
    }
    .button{
        margin-top:15%;
        display:flex;
    }
    .line{
        width:20%;
        margin-left: 3%;
    }
    .title{
        margin-bottom:20%;
    }
    .optionScroll{
        position:relative;
    }
    .scroll{
        display: block;
        position: absolute;
        top: 30%;
        left: 50%;
        transform: translateX(-50%);
        margin-top: 30px;
        width: 15px;
        height: 313px;
        overflow: hidden;
        opacity: 1;
        transition: opacity 2s 0.5s;
    }
    .scroll:before{
        content: '';
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 200%;
    background-repeat: repeat-y;
    animation: a-scrollHint 30s linear infinite;
}


